mapper.select:
select * from ( select rownum as rnum, ma_no, m_addr ,m_no, m_id, m_name, o_num, o_no, o_cnt, o_price, o_mode, o_confirm, o_date, p_no from ( select ttt.*, replace(m_addr, '#!@@!#', ' ') m_addr from ( select ma_no ,t1.m_no, m_id, m_name, o_num, o_no, o_cnt, o_price, o_mode, o_confirm, to_char(o_date, 'yyyy-MM-dd hh24:mm:ss') o_date, p_no from (select * from product_order) t1 inner join (select * from member) t2 on (t1.m_no = t2.m_no)
o_date >= #{startDate}
o_date < to_date(#{endDate}, 'YYYY-MM-DD') + 1
o_date >= #{startDate} and o_date < to_date(#{endDate}, 'YYYY-MM-DD') + 1
) ttt inner join member_addr ttt2 on ttt.ma_no = ttt2.ma_no order by o_num desc) where rownum <= #{end_data}) where rnum >= #{start_data}
